function parseMarkdown(){document.querySelector("#preview").innerHTML=marked.parse(document.querySelector("#editor").value,{renderer:renderer,gfm:!0,breaks:!0})}const projectName="markdown-previewer";localStorage.setItem("example_project","Markdown Previewer");const renderer=new marked.Renderer,linkRenderer=renderer.link;renderer.link=function(e,r,n){const o=linkRenderer.call(renderer,e,r,n);return o.replace(/^<a /,'<a target="_blank" rel="nofollow" ')},document.addEventListener("DOMContentLoaded",e=>{parseMarkdown(),["change","keypress","keyup","keydown"].forEach(e=>{document.querySelector("#editor").addEventListener(e,e=>{parseMarkdown()})})});